# frozen_string_literal: true

require "spec_helper"
require "pathname"
require "shellwords"

module Contents
  FOO_RBI = <<~CONTENTS
    # This file is autogenerated. Do not edit it by hand. Regenerate it with:
    #   generate command

    # typed: true

    module Foo
      def self.bar(a = _, b: _, **opts); end
    end

    Foo::PI = T.let(T.unsafe(nil), Float)
  CONTENTS

  BAR_RBI = <<~CONTENTS
    # This file is autogenerated. Do not edit it by hand. Regenerate it with:
    #   generate command

    # typed: true

    module Bar
      def self.bar(a = _, b: _, **opts); end
    end

    Bar::PI = T.let(T.unsafe(nil), Float)
  CONTENTS

  BAZ_RBI = <<~CONTENTS
    # This file is autogenerated. Do not edit it by hand. Regenerate it with:
    #   generate command

    # typed: true

    module Baz
    end

    class Baz::Test
      def fizz; end
    end
  CONTENTS
end

RSpec.describe(Tapioca::Cli) do
  let(:outdir) { @outdir }
  let(:repo_path) { @repo_path }

  def run(command, args = [], flags = {})
    flags = {
      outdir: outdir,
      generate_command: "'generate command'",
    }.merge(flags).flat_map { |k, v| ["--#{k}", v.to_s] }

    exec_command = [
      "bundle",
      "exec",
      "bin/tapioca",
      command,
      *flags,
      *args,
    ]

    Bundler.with_clean_env do
      IO.popen(
        exec_command.join(' '),
        chdir: repo_path
      ).read
    end
  end

  before(:all) do
    @repo_path = (Pathname.new(__dir__) / ".." / "support" / "repo").expand_path
    Bundler.with_clean_env do
      IO.popen(
        ["bundle", "install", "--quiet"],
        chdir: @repo_path
      ).read
    end
  end

  around(:each) do |example|
    FileUtils.rm_rf(repo_path / "sorbet")
    Dir.mktmpdir do |outdir|
      @outdir = outdir
      example.run
    end
    FileUtils.rm_rf(repo_path / "sorbet")
  end

  describe("#init") do
    it 'must create proper files' do
      output = run("init")

      expect(output).to(eq(<<-OUTPUT))
      create  sorbet/config
      create  sorbet/tapioca/require.rb
      OUTPUT

      expect(File).to(exist(repo_path / "sorbet/config"))
      expect(File.read(repo_path / "sorbet/config")).to(eq(<<~CONTENTS))
        --dir
        .
      CONTENTS
      expect(File).to(exist(repo_path / "sorbet/tapioca/require.rb"))
      expect(File.read(repo_path / "sorbet/tapioca/require.rb")).to(eq(<<~CONTENTS))
        # frozen_string_literal: true
        # typed: false

        # Add your extra requires here
      CONTENTS
    end

    it 'must not overwrite files' do
      FileUtils.mkdir_p(repo_path / "sorbet/tapioca")
      FileUtils.touch([
        repo_path / "sorbet/config",
        repo_path / "sorbet/tapioca/require.rb",
      ])

      output = run("init")

      expect(output).to(eq(<<-OUTPUT))
        skip  sorbet/config
        skip  sorbet/tapioca/require.rb
      OUTPUT

      expect(File.read(repo_path / "sorbet/config")).to(be_empty)
      expect(File.read(repo_path / "sorbet/tapioca/require.rb")).to(be_empty)
    end
  end

  describe("#todo") do
    before(:each) do
      run("init")
    end

    it 'does nothing if all constant are already resolved' do
      output = run("todo")

      expect(output).to(eq(<<~OUTPUT))
        Compiling sorbet/rbi/todo.rbi, this may take a few seconds... Nothing to do
      OUTPUT

      expect(File).not_to(exist(repo_path / "sorbet/rbi/todo.rbi"))
    end

    it 'creates a list of all unresolved constants' do
      File.write(repo_path / "file.rb", <<~RUBY)
        class Foo < ::Undef1
          def foo
            Undef2.new
          end
        end

        ::Undef1::Undef3.foo
        Undef2::Undef4.bar
      RUBY

      output = run("todo")

      File.delete(repo_path / "file.rb")

      expect(output).to(eq(<<~CONTENTS))
        Compiling sorbet/rbi/todo.rbi, this may take a few seconds... Done
        All unresolved constants have been written to sorbet/rbi/todo.rbi.
        Please review changes and commit them.
      CONTENTS

      expect(File).to(exist(repo_path / "sorbet/rbi/todo.rbi"))
      expect(File.read(repo_path / "sorbet/rbi/todo.rbi")).to(eq(<<~CONTENTS))
        # This file is autogenerated. Do not edit it by hand. Regenerate it with:
        #   generate command

        # typed: false

        module ::Undef1; end
        module ::Undef1::Undef3; end
        module ::Undef2::Undef4; end
        module Foo::Undef2; end
      CONTENTS
    end

    it 'deletes the todo.rbi file when everything is resolved' do
      FileUtils.mkdir_p(repo_path / "sorbet/rbi")
      File.write(repo_path / "sorbet/rbi/todo.rbi", <<-RBI)
        # This file is autogenerated. Do not edit it by hand. Regenerate it with:
        #   generate command

        # typed: false

        module ::Undef1; end
        module ::Undef1::Undef2; end
        module ::Undef2::Undef4; end
        module Foo::Undef2; end
      RBI

      output = run("todo")

      expect(output).to(eq(<<~OUTPUT))
        Compiling sorbet/rbi/todo.rbi, this may take a few seconds... Nothing to do
      OUTPUT

      expect(File).not_to(exist(repo_path / "sorbet/rbi/todo.rbi"))
    end
  end

  describe("#generate") do
    before(:each) do
      run("init")
    end

    it 'must generate a single gem RBI' do
      output = run("generate", "foo")

      expect(output).to(include(<<~OUTPUT))
        Processing 'foo' gem:
          Compiling foo, this may take a few seconds...   Done
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File.read("#{outdir}/foo@0.0.1.rbi")).to(eq(Contents::FOO_RBI))

      expect(File).to_not(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to_not(exist("#{outdir}/baz@0.0.2.rbi"))
    end

    it 'must perform postrequire properly' do
      output = run("generate", "foo", postrequire: repo_path / "postrequire.rb")

      expect(output).to(include(<<~OUTPUT))
        Processing 'foo' gem:
          Compiling foo, this may take a few seconds...   Done
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File.read("#{outdir}/foo@0.0.1.rbi")).to(eq(template(<<~CONTENTS)))
        #{Contents::FOO_RBI}
        class Foo::Secret
        end

        <% if ruby_version(">= 2.4.0") %>
        Foo::Secret::VALUE = T.let(T.unsafe(nil), Integer)
        <% else %>
        Foo::Secret::VALUE = T.let(T.unsafe(nil), Fixnum)
        <% end %>
      CONTENTS

      expect(File).to_not(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to_not(exist("#{outdir}/baz@0.0.2.rbi"))
    end

    it 'must generate multiple gem RBIs' do
      output = run("generate", ["foo", "bar"])

      expect(output).to(include(<<~OUTPUT))
        Processing 'foo' gem:
          Compiling foo, this may take a few seconds...   Done
      OUTPUT

      expect(output).to(include(<<~OUTPUT))
        Processing 'bar' gem:
          Compiling bar, this may take a few seconds...   Done
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to(exist("#{outdir}/bar@0.3.0.rbi"))

      expect(File.read("#{outdir}/foo@0.0.1.rbi")).to(eq(Contents::FOO_RBI))
      expect(File.read("#{outdir}/bar@0.3.0.rbi")).to(eq(Contents::BAR_RBI))

      expect(File).to_not(exist("#{outdir}/baz@0.0.2.rbi"))
    end

    it 'must generate RBIs for all gems in the Gemfile' do
      output = run("generate")

      expect(output).to(include(<<~OUTPUT))
        Processing 'bar' gem:
          Compiling bar, this may take a few seconds...   Done
      OUTPUT

      expect(output).to(include(<<~OUTPUT))
        Processing 'baz' gem:
          Compiling baz, this may take a few seconds...   Done
      OUTPUT

      expect(output).to(include(<<~OUTPUT))
        Processing 'foo' gem:
          Compiling foo, this may take a few seconds...   Done
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))

      expect(File.read("#{outdir}/foo@0.0.1.rbi")).to(eq(Contents::FOO_RBI))
      expect(File.read("#{outdir}/bar@0.3.0.rbi")).to(eq(Contents::BAR_RBI))
      expect(File.read("#{outdir}/baz@0.0.2.rbi")).to(eq(Contents::BAZ_RBI))
    end

    it 'must respect exclude option' do
      output = run("generate", "", exclude: "foo bar")

      expect(output).to_not(include(<<~OUTPUT))
        Processing 'bar' gem:
          Compiling bar, this may take a few seconds...   Done
      OUTPUT

      expect(output).to(include(<<~OUTPUT))
        Processing 'baz' gem:
          Compiling baz, this may take a few seconds...   Done
      OUTPUT

      expect(output).to_not(include(<<~OUTPUT))
        Processing 'foo' gem:
          Compiling foo, this may take a few seconds...   Done
      OUTPUT

      expect(File).to_not(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to_not(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))

      expect(File.read("#{outdir}/baz@0.0.2.rbi")).to(eq(Contents::BAZ_RBI))
    end

    it 'does not crash when the extras gem is loaded' do
      File.write(repo_path / "sorbet/tapioca/require.rb", 'require "extras/all"')
      output = run("generate", "foo")

      expect(output).to(include(<<~OUTPUT))
        Processing 'foo' gem:
          Compiling foo, this may take a few seconds...   Done
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File.read("#{outdir}/foo@0.0.1.rbi")).to(eq(Contents::FOO_RBI))

      File.delete(repo_path / "sorbet/tapioca/require.rb")
    end
  end

  describe("#sync") do
    before(:each) do
      run("init")
    end

    it 'must perform no operations if everything is up-to-date' do
      run("generate")

      output = run("sync")

      expect(output).to_not(include("-- Removing:"))
      expect(output).to_not(include("++ Adding:"))
      expect(output).to_not(include("-> Moving:"))

      expect(output).to(include(<<~OUTPUT))
        Removing RBI files of gems that have been removed:

          Nothing to do.
      OUTPUT
      expect(output).to(include(<<~OUTPUT))
        Generating RBI files of gems that are added or updated:

          Nothing to do.
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))
    end

    it 'must respect exclude option' do
      run("generate")

      output = run("sync", "", exclude: "foo bar")

      expect(output).to(include("-- Removing: #{outdir}/foo@0.0.1.rbi\n"))
      expect(output).to(include("-- Removing: #{outdir}/bar@0.3.0.rbi\n"))
      expect(output).to_not(include("-- Removing: #{outdir}/baz@0.0.2.rbi\n"))
      expect(output).to_not(include("++ Adding:"))
      expect(output).to_not(include("-> Moving:"))

      expect(output).to_not(include(<<~OUTPUT))
        Removing RBI files of gems that have been removed:

          Nothing to do.
      OUTPUT
      expect(output).to(include(<<~OUTPUT))
        Generating RBI files of gems that are added or updated:

          Nothing to do.
      OUTPUT

      expect(File).to_not(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to_not(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))
    end

    it 'must remove outdated RBIs' do
      run("generate")
      FileUtils.touch("#{outdir}/outdated@5.0.0.rbi")

      output = run("sync")

      expect(output).to(include("-- Removing: #{outdir}/outdated@5.0.0.rbi\n"))
      expect(output).to_not(include("++ Adding:"))
      expect(output).to_not(include("-> Moving:"))

      expect(output).to(include(<<~OUTPUT))
        Generating RBI files of gems that are added or updated:

          Nothing to do.
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))
      expect(File).to_not(exist("#{outdir}/outdated@5.0.0.rbi"))
    end

    it 'must add missing RBIs' do
      %w{
        foo@0.0.1.rbi
      }.each do |rbi|
        FileUtils.touch("#{outdir}/#{rbi}")
      end

      output = run("sync")

      expect(output).to(include("++ Adding: #{outdir}/bar@0.3.0.rbi\n"))
      expect(output).to(include("++ Adding: #{outdir}/baz@0.0.2.rbi\n"))
      expect(output).to_not(include("-- Removing:"))
      expect(output).to_not(include("-> Moving:"))

      expect(output).to(include(<<~OUTPUT))
        Removing RBI files of gems that have been removed:

          Nothing to do.
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))
    end

    it 'must move outdated RBIs' do
      %w{
        foo@0.0.1.rbi
        bar@0.0.1.rbi
        baz@0.0.1.rbi
      }.each do |rbi|
        FileUtils.touch("#{outdir}/#{rbi}")
      end

      output = run("sync")

      expect(output).to(include("-> Moving: #{outdir}/bar@0.0.1.rbi to #{outdir}/bar@0.3.0.rbi\n"))
      expect(output).to(include("++ Adding: #{outdir}/bar@0.3.0.rbi\n"))
      expect(output).to(include("-> Moving: #{outdir}/baz@0.0.1.rbi to #{outdir}/baz@0.0.2.rbi\n"))
      expect(output).to(include("++ Adding: #{outdir}/baz@0.0.2.rbi\n"))
      expect(output).to_not(include("-- Removing:"))

      expect(output).to(include(<<~OUTPUT))
        Removing RBI files of gems that have been removed:

          Nothing to do.
      OUTPUT

      expect(File).to(exist("#{outdir}/foo@0.0.1.rbi"))
      expect(File).to(exist("#{outdir}/bar@0.3.0.rbi"))
      expect(File).to(exist("#{outdir}/baz@0.0.2.rbi"))

      expect(File).to_not(exist("#{outdir}/bar@0.0.1.rbi"))
      expect(File).to_not(exist("#{outdir}/baz@0.0.1.rbi"))
    end
  end
end
